package com.redhat.coolstore.model;

import java.io.Serializable;
import java.util.ArrayList;
import java.util.List;
import javax.enterprise.context.Dependent;

@Dependent
public class ShoppingCart implements Serializable {
   private static final long serialVersionUID = -1108043957592113528L;
   private double cartItemTotal;
   private double cartItemPromoSavings;
   private double shippingTotal;
   private double shippingPromoSavings;
   private double cartTotal;
   private List<ShoppingCartItem> shoppingCartItemList = new ArrayList();

   public List<ShoppingCartItem> getShoppingCartItemList() {
      return this.shoppingCartItemList;
   }

   public void setShoppingCartItemList(List<ShoppingCartItem> shoppingCartItemList) {
      this.shoppingCartItemList = shoppingCartItemList;
   }

   public void resetShoppingCartItemList() {
      this.shoppingCartItemList = new ArrayList();
   }

   public void addShoppingCartItem(ShoppingCartItem sci) {
      if (sci != null) {
         this.shoppingCartItemList.add(sci);
      }

   }

   public boolean removeShoppingCartItem(ShoppingCartItem sci) {
      boolean removed = false;
      if (sci != null) {
         removed = this.shoppingCartItemList.remove(sci);
      }

      return removed;
   }

   public double getCartItemTotal() {
      return this.cartItemTotal;
   }

   public void setCartItemTotal(double cartItemTotal) {
      this.cartItemTotal = cartItemTotal;
   }

   public double getShippingTotal() {
      return this.shippingTotal;
   }

   public void setShippingTotal(double shippingTotal) {
      this.shippingTotal = shippingTotal;
   }

   public double getCartTotal() {
      return this.cartTotal;
   }

   public void setCartTotal(double cartTotal) {
      this.cartTotal = cartTotal;
   }

   public double getCartItemPromoSavings() {
      return this.cartItemPromoSavings;
   }

   public void setCartItemPromoSavings(double cartItemPromoSavings) {
      this.cartItemPromoSavings = cartItemPromoSavings;
   }

   public double getShippingPromoSavings() {
      return this.shippingPromoSavings;
   }

   public void setShippingPromoSavings(double shippingPromoSavings) {
      this.shippingPromoSavings = shippingPromoSavings;
   }

   public String toString() {
      return "ShoppingCart [cartItemTotal=" + this.cartItemTotal + ", cartItemPromoSavings=" + this.cartItemPromoSavings + ", shippingTotal=" + this.shippingTotal + ", shippingPromoSavings=" + this.shippingPromoSavings + ", cartTotal=" + this.cartTotal + ", shoppingCartItemList=" + this.shoppingCartItemList + "]";
   }
}
