package com.redhat.coolstore.service;

import com.redhat.coolstore.model.ShoppingCart;
import javax.ejb.Stateless;

@Stateless
public class ShippingService {
   public void calculateShipping(ShoppingCart sc) {
      if (sc != null) {
         if (sc.getCartItemTotal() >= 0.0D && sc.getCartItemTotal() < 25.0D) {
            sc.setShippingTotal(2.99D);
         } else if (sc.getCartItemTotal() >= 25.0D && sc.getCartItemTotal() < 50.0D) {
            sc.setShippingTotal(4.99D);
         } else if (sc.getCartItemTotal() >= 50.0D && sc.getCartItemTotal() < 75.0D) {
            sc.setShippingTotal(6.99D);
         } else if (sc.getCartItemTotal() >= 75.0D && sc.getCartItemTotal() < 100.0D) {
            sc.setShippingTotal(8.99D);
         } else if (sc.getCartItemTotal() >= 100.0D && sc.getCartItemTotal() < 10000.0D) {
            sc.setShippingTotal(10.99D);
         }
      }

   }
}
