package com.redhat.coolstore.model;

import java.io.Serializable;
import java.util.ArrayList;
import java.util.List;
import javax.persistence.CascadeType;
import javax.persistence.Column;
import javax.persistence.Entity;
import javax.persistence.FetchType;
import javax.persistence.GeneratedValue;
import javax.persistence.Id;
import javax.persistence.JoinColumn;
import javax.persistence.OneToMany;
import javax.persistence.Table;

@Entity
@Table(
   name = "ORDERS"
)
public class Order implements Serializable {
   private static final long serialVersionUID = -1L;
   @Id
   @GeneratedValue
   private long orderId;
   private String customerName;
   private String customerEmail;
   private double orderValue;
   private double retailPrice;
   private double discount;
   private double shippingFee;
   private double shippingDiscount;
   @Column(
      name = "TOTAL_PRICE"
   )
   @OneToMany(
      fetch = FetchType.EAGER,
      cascade = {CascadeType.ALL},
      orphanRemoval = true
   )
   @JoinColumn(
      name = "ORDER_ID"
   )
   private List<OrderItem> itemList = new ArrayList();

   public long getOrderId() {
      return this.orderId;
   }

   public void setOrderId(long orderId) {
      this.orderId = orderId;
   }

   public String getCustomerName() {
      return this.customerName;
   }

   public void setCustomerName(String customerName) {
      this.customerName = customerName;
   }

   public String getCustomerEmail() {
      return this.customerEmail;
   }

   public void setCustomerEmail(String customerEmail) {
      this.customerEmail = customerEmail;
   }

   public double getOrderValue() {
      return this.orderValue;
   }

   public void setOrderValue(double orderValue) {
      this.orderValue = orderValue;
   }

   public double getRetailPrice() {
      return this.retailPrice;
   }

   public void setRetailPrice(double retailPrice) {
      this.retailPrice = retailPrice;
   }

   public double getDiscount() {
      return this.discount;
   }

   public void setDiscount(double discount) {
      this.discount = discount;
   }

   public double getShippingFee() {
      return this.shippingFee;
   }

   public void setShippingFee(double shippingFee) {
      this.shippingFee = shippingFee;
   }

   public double getShippingDiscount() {
      return this.shippingDiscount;
   }

   public void setShippingDiscount(double shippingDiscount) {
      this.shippingDiscount = shippingDiscount;
   }

   public void setItemList(List<OrderItem> itemList) {
      this.itemList = itemList;
   }

   public List<OrderItem> getItemList() {
      return this.itemList;
   }

   public String toString() {
      return "Order [orderId=" + this.orderId + ", customerName=" + this.customerName + ", customerEmail=" + this.customerEmail + ", orderValue=" + this.orderValue + ", retailPrice=" + this.retailPrice + ", discount=" + this.discount + ", shippingFee=" + this.shippingFee + ", shippingDiscount=" + this.shippingDiscount + ", itemList=" + this.itemList + "]";
   }
}
